while (totalStars > k) {
  int diff = totalStars - k;
  List<Star> furthest = neighbors.get(neighbors.lastKey());
  if (furthest.size() <= diff) {
    totalStars -= furthest.size();
    neighbors.pollLastEntry();
  } else {
    Collections.shuffle(furthest);
    for (int i = 0; i < diff; i++) {
      furthest.remove(0);
    }
    totalStars -= diff;
  }
}
